import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const searchImages = async () => {
    if (!searchTerm.trim()) {
      alert("Please enter a search term");
      return;
    }

    setLoading(true);
    try {
      const PIXABAY_API_KEY = "49707751-675bfff183d462455a7b5907c";
      const url = `https://pixabay.com/api/?key=${PIXABAY_API_KEY}&q=${encodeURIComponent(
        searchTerm
      )}&image_type=photo`;

      const res = await fetch(url);
      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`Pixabay API Error: ${res.status} - ${errorText}`);
      }

      const data = await res.json();
      setImages(data.hits);
    } catch (err) {
      console.error("Error fetching images:", err.message);
      alert("Failed to fetch images. Check your API key or try again later.");
    }
    setLoading(false);
  };

  const handleAddCaptions = (imgUrl) => {
    navigate("/editor", { state: { imgUrl } });
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800">Image Search</h1>

      {/* Search input */}
      <div className="flex justify-center mb-6">
        <input
          type="text"
          className="border border-gray-300 p-3 rounded-lg w-80 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Search for images..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button
          onClick={searchImages}
          className="ml-4 bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-all duration-200"
        >
          Search
        </button>
      </div>

      {/* Loading state */}
      {loading ? (
        <div className="flex justify-center">
          <div className="w-12 h-12 border-4 border-t-4 border-blue-600 rounded-full animate-spin"></div>
        </div>
      ) : (
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-6">
          {images.map((img) => (
            <div key={img.id} className="border rounded-lg shadow-md overflow-hidden">
              <img
                src={img.previewURL}
                alt={img.tags}
                className="w-full h-64 object-cover"
              />
              <div className="p-4">
                <button
                  onClick={() => handleAddCaptions(img.largeImageURL)}
                  className="bg-green-600 text-white px-4 py-2 rounded-lg w-full hover:bg-green-700 transition-all duration-200"
                >
                  Add Captions
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchPage;
