import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const SearchPage = () => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const navigate = useNavigate();

  const handleSearch = async () => {
    const res = await fetch(
      `https://pixabay.com/api/?key=36116218-fad8a8a8b1c78bdb73868f804&q=${query}&image_type=photo`
    );
    const data = await res.json();
    setResults(data.hits);
  };

  const handleEdit = (imgUrl) => {
    navigate("/editor", { state: { imgUrl } });
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-2">Image Search</h1>
      <input
        className="border p-2 mr-2"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search images"
      />
      <button className="bg-blue-500 text-white px-4 py-2" onClick={handleSearch}>
        Search
      </button>
      <div className="grid grid-cols-3 gap-4 mt-4">
        {results.map((img) => (
          <div key={img.id} className="border p-2">
            <img src={img.webformatURL} alt="" className="w-full" />
            <button
              onClick={() => handleEdit(img.largeImageURL)}
              className="mt-2 bg-green-500 text-white px-2 py-1"
            >
              Add Captions
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchPage;
