import React, { useEffect, useRef } from "react";
import { useLocation } from "react-router-dom";
import { fabric } from "fabric";

const EditorPage = () => {
  const canvasRef = useRef(null);
  const fabricCanvasRef = useRef(null);
  const location = useLocation();
  const imgUrl = location.state?.imgUrl;

  useEffect(() => {
    if (!imgUrl) return;

    // Dispose previous canvas if it exists
    if (fabricCanvasRef.current) {
      fabricCanvasRef.current.dispose();
    }

    const canvas = new fabric.Canvas(canvasRef.current, {
      width: 800,
      height: 500,
    });
    fabricCanvasRef.current = canvas;

    fabric.Image.fromURL(imgUrl, (img) => {
      img.set({
        left: 0,
        top: 0,
        selectable: false,
      });
      img.scaleToWidth(800);
      canvas.setBackgroundImage(img, canvas.renderAll.bind(canvas));
    });

    // Cleanup
    return () => {
      canvas.dispose();
    };
  }, [imgUrl]);

  const addText = () => {
    const canvas = fabricCanvasRef.current;
    const text = new fabric.IText("Edit me", {
      left: 50,
      top: 50,
      fontSize: 24,
      fill: "black",
    });
    canvas.add(text).setActiveObject(text);
  };

  const addRectangle = () => {
    const canvas = fabricCanvasRef.current;
    const rect = new fabric.Rect({
      left: 100,
      top: 100,
      fill: "rgba(255, 0, 0, 0.5)",
      width: 120,
      height: 80,
    });
    canvas.add(rect);
  };

  const addCircle = () => {
    const canvas = fabricCanvasRef.current;
    const circle = new fabric.Circle({
      left: 150,
      top: 150,
      radius: 50,
      fill: "rgba(0, 0, 255, 0.5)",
    });
    canvas.add(circle);
  };

  const addTriangle = () => {
    const canvas = fabricCanvasRef.current;
    const triangle = new fabric.Triangle({
      left: 200,
      top: 200,
      width: 100,
      height: 100,
      fill: "rgba(0, 255, 0, 0.5)",
    });
    canvas.add(triangle);
  };

  const addPolygon = () => {
    const canvas = fabricCanvasRef.current;
    const polygon = new fabric.Polygon(
      [
        { x: 200, y: 100 },
        { x: 250, y: 150 },
        { x: 300, y: 100 },
        { x: 275, y: 50 },
        { x: 225, y: 50 },
      ],
      {
        left: 250,
        top: 100,
        fill: "rgba(255, 255, 0, 0.5)",
      }
    );
    canvas.add(polygon);
  };

  const downloadImage = () => {
    const canvas = fabricCanvasRef.current;
    const dataURL = canvas.toDataURL({
      format: "png",
      quality: 1,
    });

    const link = document.createElement("a");
    link.href = dataURL;
    link.download = "edited-image.png";
    link.click();
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Edit Image</h2>

      <div className="mb-4 flex flex-wrap gap-2">
        <button onClick={addText} className="bg-blue-500 text-white px-3 py-1 rounded">
          Add Text
        </button>
        <button onClick={addRectangle} className="bg-green-500 text-white px-3 py-1 rounded">
          Add Rectangle
        </button>
        <button onClick={addCircle} className="bg-purple-500 text-white px-3 py-1 rounded">
          Add Circle
        </button>
        <button onClick={addTriangle} className="bg-pink-500 text-white px-3 py-1 rounded">
          Add Triangle
        </button>
        <button onClick={addPolygon} className="bg-yellow-500 text-black px-3 py-1 rounded">
          Add Polygon
        </button>
        <button onClick={downloadImage} className="bg-black text-white px-3 py-1 rounded">
          Download Image
        </button>
      </div>

      <canvas ref={canvasRef} id="fabricCanvas" />
    </div>
  );
};

export default EditorPage;
