import React, { useEffect, useRef, useState } from "react";
import { useLocation } from "react-router-dom";
import {
  Canvas,
  Image,
  Rect,
  Circle,
  Triangle,
  Polygon,
  Textbox
} from "fabric";

const EditorPage = () => {
  const location = useLocation();
  const canvasRef = useRef(null);
  const [canvas, setCanvas] = useState(null);
  const [layers, setLayers] = useState([]);

  useEffect(() => {
    const c = new Canvas("fabricCanvas", {
      height: 500,
      width: 800,
    });
    setCanvas(c);
  }, []);

  useEffect(() => {
    if (canvas && location.state?.imgUrl) {
      Image.fromURL(location.state.imgUrl, (img) => {
        canvas.setBackgroundImage(img, canvas.renderAll.bind(canvas), {
          scaleX: canvas.width / img.width,
          scaleY: canvas.height / img.height,
        });
      });
    }
  }, [canvas, location]);

  const addText = () => {
    const text = new Textbox("Your Text", {
      left: 100,
      top: 100,
      width: 200,
      fontSize: 20,
      fill: "black",
    });
    canvas.add(text);
    logLayers();
  };

  const addShape = (type) => {
    let shape;
    if (type === "rect") {
      shape = new Rect({ left: 150, top: 150, fill: "red", width: 100, height: 100 });
    } else if (type === "circle") {
      shape = new Circle({ left: 150, top: 150, fill: "blue", radius: 50 });
    } else if (type === "triangle") {
      shape = new Triangle({ left: 150, top: 150, fill: "green", width: 100, height: 100 });
    } else if (type === "polygon") {
      shape = new Polygon([
        { x: 200, y: 0 },
        { x: 250, y: 100 },
        { x: 150, y: 100 },
      ], {
        fill: 'purple',
        left: 150,
        top: 150,
      });
    }
    canvas.add(shape);
    logLayers();
  };

  const downloadImage = () => {
    const dataURL = canvas.toDataURL({ format: "png" });
    const link = document.createElement("a");
    link.href = dataURL;
    link.download = "edited-image.png";
    link.click();
  };

  const logLayers = () => {
    const objects = canvas.getObjects();
    const layersData = objects.map(obj => obj.toObject());
    setLayers(layersData);
    console.log("Layers:", layersData);
  };

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-2">Image Editor</h1>
      <div className="mb-2 space-x-2">
        <button onClick={addText} className="bg-gray-800 text-white px-3 py-1">Add Text</button>
        <button onClick={() => addShape("rect")} className="bg-red-500 text-white px-3 py-1">Rectangle</button>
        <button onClick={() => addShape("circle")} className="bg-blue-500 text-white px-3 py-1">Circle</button>
        <button onClick={() => addShape("triangle")} className="bg-green-500 text-white px-3 py-1">Triangle</button>
        <button onClick={() => addShape("polygon")} className="bg-purple-500 text-white px-3 py-1">Polygon</button>
        <button onClick={downloadImage} className="bg-yellow-500 text-white px-3 py-1">Download</button>
      </div>
      <canvas id="fabricCanvas" ref={canvasRef}></canvas>
    </div>
  );
};

export default EditorPage;
